// App.jsx ‚Äî S-Forecast ver.3.0-cef (Navier‚ÄìRiemann‚ÄìCEF Hybrid Edition)
import React, { useEffect, useState } from "react";
import "./App.css";

export default function App() {
  const API_KEY = "8370f7e693e34a79bdd180327252510";

  const CITY = {
    ÏÑúÏö∏:   { name_en: "Seoul", lat: 37.5665, lon: 126.9780, alt: 20 },
    ÏàòÏõê:   { name_en: "Suwon", lat: 37.2636, lon: 127.0286, alt: 30 },
    Ïö©Ïù∏:   { name_en: "Yongin", lat: 37.2753, lon: 127.1159, alt: 70 },
    ÏïàÏÇ∞:   { name_en: "Ansan", lat: 37.3219, lon: 126.8309, alt: 15 },
    ÏïàÏñë:   { name_en: "Anyang", lat: 37.3943, lon: 126.9568, alt: 25 },
    Í∞ïÎ¶â:   { name_en: "Gangneung", lat: 37.7519, lon: 128.8761, alt: 50 },
    Î∂ÄÏÇ∞:   { name_en: "Busan", lat: 35.1796, lon: 129.0756, alt: 5 },
    Ïò§ÏÇ¨Ïπ¥: { name_en: "Osaka", lat: 34.6937, lon: 135.5023, alt: 10 },
    ÌõÑÏø†Ïò§Ïπ¥:{ name_en: "Fukuoka", lat: 33.5902, lon: 130.4017, alt: 20 },
    Ïú†ÌõÑÏù∏: { name_en: "Yufuin", lat: 33.2659, lon: 131.3461, alt: 150 },
    ÎÇòÍ≥†Ïïº: { name_en: "Nagoya", lat: 35.1815, lon: 136.9066, alt: 15 },
    ÎßàÏì∞ÏïºÎßà:{ name_en: "Matsuyama", lat: 33.8393, lon: 132.7657, alt: 30 },
  };

  const [city, setCity] = useState("ÏàòÏõê");
  const [forecast, setForecast] = useState([]);
  const [hourly, setHourly] = useState([]);
  const [status, setStatus] = useState("Loading‚Ä¶");

  const WEEK = ["Sun","Mon","Tue","Wed","Thu","Fri","Sat"];
  const dayStr = (iso) => {
    const d = new Date(iso);
    return `${d.getMonth() + 1}/${d.getDate()} (${WEEK[d.getDay()]})`;
  };

  function refineCondition(text) {
    const t = text?.toLowerCase() || "";
    if (t.includes("sunny") || t.includes("clear")) return "ÎßëÏùå";
    if (t.includes("partly") || t.includes("mostly")) return "ÎåÄÏ≤¥Î°ú ÎßëÏùå";
    if (t.includes("cloud")) return "Í∞ÄÎÅî Íµ¨Î¶Ñ ÎßéÏùå";
    if (t.includes("overcast")) return "ÎåÄÏ≤¥Î°ú ÌùêÎ¶º";
    if (t.includes("rain") && t.includes("snow")) return "ÎπÑ ÎòêÎäî Îàà";
    if (t.includes("rain") || t.includes("drizzle") || t.includes("shower")) return "ÎπÑ ÎòêÎäî ÏÜåÎÇòÍ∏∞";
    if (t.includes("snow") || t.includes("sleet")) return "Îàà";
    if (t.includes("fog") || t.includes("mist") || t.includes("haze")) return "ÏïàÍ∞ú";
    return "Í∏∞ÌÉÄ";
  }

  // -----------------------------
  // üîπ Nonlinear S (Navier‚ÄìRiemann Base)
  // -----------------------------
  function riemannZetaApprox(s, n = 40) {
    let sum = 0;
    for (let k = 1; k <= n; k++) sum += 1 / Math.pow(k, s);
    return sum;
  }

  function computeSPlusRiemann({ temp, humidity, wind = 0, cloud = 0 }) {
    const T0 = 20, H0 = 50;
    const sigmaT = 7, sigmaH = 15;
    const v_c = 10, rho0 = 1.2, kappa = 0.4;

    const ST = Math.exp(-((temp - T0) ** 2) / (2 * sigmaT ** 2));
    const SH = Math.exp(-((humidity - H0) ** 2) / (2 * sigmaH ** 2));
    const SC = 1 - cloud / 100;
    const SV = Math.tanh(wind / v_c);
    const baseS = rho0 * (ST * SH * SC) - kappa * SV;

    const sigma = 1 + 0.1 * Math.sin(temp / 10) - 0.05 * Math.cos(humidity / 30);
    const zeta = riemannZetaApprox(sigma);
    const corr = 1 + 0.02 * Math.tanh(zeta / 20);
    const S = baseS * corr;

    return Math.max(0, Math.min(3, S));
  }

  // -----------------------------
  // üîπ CEF Rhythm Correction Layer
  // -----------------------------
  function computeSPlusCEF({ temp, humidity, wind = 0, cloud = 0 }) {
    const Œ± = 0.8, Œ≤ = 0.6;
    const dœÅ_dt = (humidity - 50) / 100; // existence density gradient
    const gradŒ¶ = (temp - 20) / 25 + (wind / 10) - (cloud / 100);
    const Scef = Œ± * dœÅ_dt ** 2 + Œ≤ * gradŒ¶ ** 2;
    return Math.min(3, Math.max(0, Scef * 1.2));
  }

  // üîπ Combined Hybrid (Navier‚ÄìRiemann + CEF)
  function computeSCombined(params) {
    const S_R = computeSPlusRiemann(params);
    const S_C = computeSPlusCEF(params);
    const weight = 0.6; // 60% CEF influence
    return (1 - weight) * S_R + weight * S_C;
  }

  function interpretRhythm(S) {
    if (S < 0.8) return "ÏïàÏ†ï ‚Äî Î¶¨Îì¨Ïù¥ ÌèâÌòïÏùÑ Ïú†ÏßÄ Ï§ë";
    if (S < 1.2) return "ÌèâÌòï ‚Äî Íµ¨Î¶Ñ ÎßéÍ≥† ÏôÑÎßåÌïú ÏßÑÎèô";
    if (S < 2.0) return "Î∂àÏïàÏ†ï ‚Äî ÎåÄÍ∏∞ ÍµêÌò∏ ÏÉÅÏäπ, ÎπÑ Í∞ÄÎä•ÏÑ±‚Üë";
    return "Ï¥àÍ≥ºÌôúÏÑ± ‚Äî Í∏âÍ≤©Ìïú Î¶¨Îì¨ Ï†ÑÌôò, Ìè≠Ìíç¬∑Í∞ïÌíç Ï£ºÏùò";
  }

  // -----------------------------
  // üîπ Fetch Weather + Apply Hybrid Model
  // -----------------------------
  async function fetchWeather(cityKo) {
    try {
      const { lat, lon } = CITY[cityKo];
      const url = `https://api.weatherapi.com/v1/forecast.json?key=${API_KEY}&q=${lat},${lon}&days=10&aqi=no&alerts=no`;
      const res = await fetch(url);
      if (!res.ok) throw new Error(`API Error ${res.status}`);

      const data = await res.json();
      const forecastDays = data.forecast?.forecastday ?? [];
      const allHours = forecastDays.flatMap(f => f.hour) ?? [];

      const nowEpoch = Math.floor(Date.now() / 1000);
      const next12h = allHours
        .filter(h => h.time_epoch >= nowEpoch)
        .slice(0, 12)
        .map(h => {
          const S = computeSCombined({
            temp: h.temp_c,
            humidity: h.humidity,
            wind: h.wind_kph,
            cloud: h.cloud,
          });
          return {
            time: new Date(h.time_epoch * 1000).toLocaleTimeString("ko-KR", { hour: "2-digit" }),
            temp: h.temp_c,
            humidity: h.humidity,
            condition: refineCondition(h.condition.text),
            wind_mps: h.wind_kph / 3.6,
            S,
            rhythm: interpretRhythm(S),
          };
        });

      const days = forecastDays.slice(0, 10).map(d => ({
        date: d.date,
        maxtemp: d.day.maxtemp_c,
        mintemp: d.day.mintemp_c,
        humidity: d.day.avghumidity,
        condition: refineCondition(d.day.condition.text),
      }));

      setHourly(next12h);
